import React, { useEffect,useState } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import '../App.css';
import RegisterForm from '../components/RegisterForm';
import LoginForm from '../components/LoginForm';
import UserList from '../components/UserList';

// const url="";
const url ='http://localhost:8001';

const User = () => {
    
    const [userList, setUserList] = useState([]);
    const [errors, setErrors] = useState([]);
    const navigate = useNavigate();
    

    const register = userParam => {
        
        axios.post(url+'/api/users/register', userParam, {withCredentials:true,})
            .then(res => {
                
                
                setUserList([...userList, res.data])
                navigate('/home');
            })
            // .catch((err)=>console.log(err))
            .catch(err=>{
                setErrors(err.response.data.errors);
            }) 
    }
    
    return (
        <div className="container">
    	
           <RegisterForm errors={errors} setErrors={setErrors} actionType={"create"} onSubmitProp={register}  initialFirstName="" initialLastName="" initialEmail="" />
            
            <LoginForm errors={errors} setErrors={setErrors}  />
           {/* <UserList userList={userList} setUserList={setUserList} /> */}
        </div>
    )
}
export default User;
