import React, {useState} from 'react';
import axios from 'axios';
import {useNavigate} from 'react-router-dom';

const LoginForm = props => {
  //keep track of what is being typed via useState hook
//   const {errors, setErrors} = props;
  const navigate = useNavigate();
  const [errors, setErrors] = useState([]);
  const [email, setEmail] = useState ("");
  const [password, setPassword] = useState ('');

  //handler when the form is submitted
  function getCookieValue(cookucoserTokename) {
    const cookies = document.cookie.split(';');
    for (let i = 0; i < cookies.length; i++) {
      const cookie = cookies[i].trim();
      if (cookie.startsWith(cookieName + '=')) {
        return cookie.substring(cookieName.length + 1);
      }
    }
    return null;
  }
  const onSubmitHandler = e => {
    //prevent default behavior of the submit
    e.preventDefault ();
    axios.post("http://localhost:8001/api/users/login",{email: email, password: password},{withCredentials:true,})
    .then(res => {console.log ("user",res.data); navigate('/home'); })
    .catch(err => {
        console.log(err);
        setErrors(err.response.data.msg);
    });
  };

  return (
    <div>
      <header>
        User Login
      </header>
      <p>{errors?errors:""}</p> 
      <form onSubmit={onSubmitHandler}>
        

        <br />
        <div className="form-fields">
          <label>Email</label><br />
          <input
            type="text"
            value={email}
            onChange={e => {
              setEmail (e.target.value);
              setErrors ([]);
            }}
          />
        </div>
        
        <br />
        <div className="form-fields">
          <label>Password</label><br />
          <input
            type="password"
            value={password}
            onChange={e => {
              setPassword (e.target.value);
              setErrors ([]);
            }}
          />
        </div>
        
        <br />
        <input className="submit-input" type="submit" value="Signin" />
      </form>
    </div>
  );
};
export default LoginForm;
