import React from 'react';
import { Redirect } from 'react-router-dom';
import useAuth from './useAuth';

const withAuth = (Component) => {
  return () => {
    const { token } = useAuth();

    if (!token) {
      return <Redirect to="/" />;
    }

    return <Component />;
  };
};

export default withAuth;
